INSERT INTO `products` (`id`, `name`, `slug`, `not_available`, `is_bestseller`, `is_aqua`, `is_new`, `description`, `meta_keywords`, `meta_description`, `created_at`, `updated_at`, `brand_name_id`) VALUES
(8, 'Рисовое мыло с углем Thann', 'risovoe-mylo-s-uglem-thann', 0, 0, 0, 0, '<p>Рисовое мыло Thann с углем и рисовым скрабом. Очищает кожу и выводит токсины. Объединяет в себе питательные свойства масла рисовых отрубей и ботанических эфирных масел. Рисовый скраб отшелушивает отмершие клетки кожи, что помогает коже выглядеть здоровой и обновленной. Уголь обеспечивает детокс эффект и балансирует выработку кожного жира. Состоит на 90% из натурального сырья.</p>\r\n', '', '', '2014-11-24 21:24:38', '2014-12-29 14:50:36', 5),
(9, 'Рисовое мыло Thann', 'risovoe-mylo-thann', 0, 0, 0, 0, 'Рисовое мыло Thann с рисовым скрабом и эфирными маслами лемонграсса и каффир лайма. Объединяет в себе питательные свойства масла рисовых отрубей и растительных эфирных масел. Масло каффир лайма очищает и увлажняет кожу. Масло лемонграсса обладает антисептическими свойствами. ', '', '', '2014-11-24 21:49:44', '2014-12-10 07:51:23', 5),
(12, 'Крем для ног "Изумительный банан" ', 'krem-dlya-nog-izumitelnyj-banan', 0, 0, 0, 0, 'Смягчающий крем для ног "Изумительный банан" / "Tender Me" Foot Cream Wonder Banana. Супер увлажняющий крем для ног содержит экстракты разных сортов банана, богатых калием и витамином А, он интенсивно питает и смягчает кожу, улучшает ее регенирацию, при регулярном использовании быстро убирает трещины на пятках. Крем сочетает восхитительный банановый аромат со свежестью мяты.', '', '', '2014-11-24 22:09:08', '2014-12-14 12:39:20', 11),
(14, 'Крем для ног от натоптышей с бананом', 'krem-dlya-nog-ot-natoptyshej-s-bananom', 0, 0, 0, 0, 'КРЕМ ДЛЯ НОГ ОТ НАТОПТЫШЕЙ С БАНАНОМ. Экстракт плая (имбиря кассумунара) обладает ранозаживляющим, антигрибковым и противоспалительным действием, помогает при спазмах и судорогах улучшает кровообращение. Натуральные увлажнители и экстракт банана прекрасно смягчает и успокаивают кожу, способствуя заживлению ран и трещин. Регулярное применение крема избавит от натоптышей и неприятного запаха, сделает кожу ступней более мягкой и ухоженной.', '', '', '2014-11-24 22:29:30', '2014-12-08 08:51:51', 8),
(16, 'Крем от загрубевшей кожи на пятках', 'krem-ot-zagrubevshej-kozhi-na-pyatkah', 0, 0, 0, 0, 'Крем для ног от загрубевшей кожи на пятках. Ваши пяточки станут бархотными. Применять каждый день на ночь. Результат будет заметен уже через 3 дня', '', '', '2014-11-24 22:33:04', '2014-12-08 08:52:24', NULL),
(20, 'Пилинг носочки Richenna', 'piling-nosochki-richenna', 0, 1, 1, 0, 'Супер новинка! Шелковые пяточки за 12 дней! Маска-пилинг для ног Richenna PEELING FOOT MASK Свойства: Richenna PEELING FOOT MASK – пилинговая маска для ног, в состав которой не входят пилинговые частицы. Вам нужно сделать 2 действия – нанести на кожу ног и смыть. Легко удаляются ороговевшие клетки, и кожа становится такой же нежной как у младенца. Фруктовая кислота (АНА) является основным ингредиентом, которая воздействует на кожу мягче, чем другие кислоты. Для облегчения пилинга она проникает внутрь омертвевших слоев кожи и воздействует на них. В состав входят натуральные экстракты 10 трав, что обеспечивает глубокое увлажнение, кожа ног мягкая и гладкая. Использование: Вымойте ноги и удалите лак сногтей. По окончанию процедуры очистки, в полиэтиленовые носки (они прилагаются) влейте лосьон Richenna и наденьте на ноги. Пусть впитывается. Рекомендуется 25 мл лосьона использовать на каждую ногу. Липкой лентой (прилагается) обмотайте ноги до лодыжки; По истечении 2 часов аккуратно снимите полиэтиленовые носки и вымойте ноги. Результат: Уже через 12 дней кожа на ногах полностью очищается от омертвевших клеток. Внимание! Не пытайтесь самостоятельно удалять старую кожу, т.к. Вы можете травмировать молодую нежную кожу.', '', '', '2014-11-24 22:39:05', '2014-12-08 08:49:54', 10),
(22, 'Смягчающий крем «Божественное манго»', 'smyagchayushij-krem-dlya-nog-bozhestvennoe-mango', 0, 0, 0, 0, 'Крем для смягчения ног, локтей и коленей «Божественное манго»/ Divine Mango Softening Cream. Этот крем, содержащий экстракт манго, который так богат витаминами и полезными веществами, эффективно устраняет сухость и шершавость кожи, прекрасно увлажняет и питает. Ваши локоточки и колени снова станут мягкими и гладкими.\r\nСпособ применения: нанесите крем на колени или локти, помассируйте до полного впитывания. Насладитесь божественным, незабываемым ароматом манго и эластичной, и очень мягкой кожей!\r\nНатуральных компонентов в составе продукта примерно 96,5%.\r\n', '', '', '2014-11-24 22:42:01', '2014-12-08 08:53:32', 11),
(26, 'Спрей для волос с экстрактом Авокадо', 'sprej-dlya-volos-s-ekstraktom-avokado', 0, 0, 1, 0, 'Спрей на основе эссенции из плодов авокадо для придания вашим волосам шелковистости, мягкости и блеска. Этот спрей-бальзам для волос укрепляет и восстанавливает ослабленные волосы, предотвращает дальнейшее их повреждение, защищая ваши волосы по всей длине от холода, жары, сухого или влажного климата при любой погоде.', '', '', '2014-12-08 09:04:03', '2014-12-20 17:12:46', 13),
(27, 'Спрей для волос с медом и йогуртом', 'sprej-dlya-volos-s-medom-i-jogurtom', 0, 0, 1, 0, 'Спрей с натуральным пчелиным медом и йогуртом для глубокого восстановления сухих и окрашенных волос. Этот бальзам для волос прежде всего предназначен для глубокого восстановления сухих, окрашенных и химически обработанных волос. Эта формула предотвращает ослабление волос, оберегает ваши волосы от высушивания, выгорания на солнце и повреждения их от жары. В результате применения этого бальзама волосы становятся мягкими и блестящими, восстанавливается натуральный цвет волос.', '', '', '2014-12-08 09:09:59', '2014-12-20 17:13:13', 13),
(28, 'Спрей для волос с рисовым молоком', 'sprej-dlya-volos-s-risovym-molokom', 0, 0, 1, 0, 'Спрей для волос с рисовым молоком. Питает ваши волосы и восстанавливают поврежденные волосы. Волосы становятся гладкими, ухоженными и увлажненными. Имеет нежный запах и подходит для любых видов волос. Наносить на влажные волосы перед сушкой,особенно тщательно уделите внимание кончикам волос.', '', '', '2014-12-08 09:12:12', '2014-12-20 17:07:58', 13),
(29, 'Спрей для волос Сила и Блеск', 'sprej-dlya-volos-sila-i-blesk', 0, 0, 1, 0, 'Спрей-сыворотка для волос для придания волосам гладкости,шелковистости и блеска. Отлично подойдет для пушащихся и безжизненных волос. Сыворотка распрямляет волосы,долше держит укладку и дарит вашим волосам блеск.', '', '', '2014-12-08 09:17:57', '2014-12-20 17:13:32', 13),
(30, 'Тоник для борьбы с выпадением волос, на травах', 'tonik-dlya-borby-s-vypadeniem-volos-na-travah', 0, 0, 1, 0, 'Тоник для борьбы с выпадением волос, на травах. Этот чудесный тоник для укрепления ослабленных волос содержит в своем составе только натуральные компоненты, целый букет из семи различных лечебных трав, которые в комплексе укрепляют корни ваших волос и уменьшают выпадение волос.\r\nЭффективно питает и увлажняет кожу головы, борется с грибком, устраняет перхоть и зуд, способствует росту новых волос благодаря стимулированию образования новых волосяных фоликул.\r\nСостав:кохия веничная;дурнишник зобовидный;экстракт из плодов мыльного дерева(эффективное противогрибковое средство);Другие травы и растения.\r\nРекомендуется использовать этот лечебный тоник для волос не менее 2 раз в день, втирая в корни волос и не смывая!\r\nЭффект будет заметен уже через 2 недели постоянного использования. ', '', '', '2014-12-08 09:24:15', '2014-12-20 17:14:12', 14),
(31, 'Отбеливающая зубная паста', 'otbelivayushaya-zubnaya-pasta', 0, 1, 0, 0, 'Зубная паста на основе натуральных компонентов с эфирными маслами. Полностью удаляет зубной налет и мелкий зубной камень. Хорошо тонизирует десна и предотвращает их кровоточивость. Прекрасный отбеливающий эффект.\r\nНе содержит консервантов и химических добавок.\r\nЭффект будет виден уже после нескольких дней использования!', '', '', '2014-12-08 11:35:14', '2014-12-08 11:35:57', 15),
(32, 'Бальзам для губ Boots Extractsа', 'balzam-dlya-gub-boots-extractsa', 0, 0, 0, 0, 'Супер питательные бальзамы для губ с натуральными маслами и экстрактами. \r\n1) Boots Extracts Almond Lip Balm / Бальзам для губ "Миндаль". Входящие в состав миндальное масло, полученное из миндального ореха, прекрасно увлажняют и питают губы, защищая их от обезвоживания и обветривания. Бальзам для губ заключен в крошечную баночку, которая идеально подходит для косметички! Изысканный десерт для Ваших губ!\r\n2) Boots Extracts Coconut Lip Balm / Бальзам для губ "Кокос". Входящие в состав масло кокоса обладает прекрасными питательными, смягчающими и увлажняющими свойствами, защищает их от обезвоживания и обветривания. Бальзам для губ заключен в крошечную баночку, которая идеально подходит для косметички! Изысканный десерт для Ваших губ!\r\n3) Boots Extracts Cocoa Butter Lip Balm / Бальзам для губ "Какао-Масло". Входящие в состав масло какао, полученные из семян какао Теоброма, прекрасно увлажняют и питают губы, защищая их от обезвоживания и обветривания. Бальзам для губ заключен в крошечную баночку, которая идеально подходит для косметички! Изысканный десерт для Ваших губ!', '', '', '2014-12-08 11:57:19', '2014-12-08 11:57:19', 6),
(33, 'Скраб для тела Boots Extracts', 'skrab-dlya-tela-boots-extracts', 0, 0, 0, 0, '<p>Boots Extracts Увлажняющий скраб для тела с натуральными экстрактами. Мягко отчищает,увлажняет и питает вашу кожу. Защищает от сухости. После этого скраба ваша кожа будет мягкая,шелковистая и увлажненная.</p>\r\n', '', '', '2014-12-08 12:17:04', '2014-12-29 14:58:03', 6),
(34, 'Крем для тела Boots Extracts', 'krem-dlya-tela-boots-extracts', 0, 0, 0, 0, 'Увлажняющее крем для тебя. Очень хорошо увлажняет и питает кожу. С натуральными экстрактами. \r\nГустой,очень увлажняющий и питательный крем отлично подойжет для сухой кожи.', '', '', '2014-12-08 12:30:21', '2014-12-08 12:35:03', 6),
(35, 'Гель для душа Boots Extracts', 'gel-dlya-dusha-boots-extracts', 0, 0, 1, 0, 'Очень питательный гель для душа с натуральными экстрактами. Хорошо увлажняет,питает и очищает кожу.', '', '', '2014-12-08 12:42:16', '2014-12-08 12:42:16', 6),
(36, 'Средство для умывания с матирующим эффектом POND''S', 'sredstvo-dlya-umyvaniya-s-matiruyushim-effektom-ponds', 0, 0, 1, 0, 'Средство для умывания с матирующим эффектом POND''S содержит минеральную глину, которая отлично впитывает в себя частички накопившейся за день пыли и глубоко очищает кожу лица. Специально разработанная формула позволяет эффективно удалять накопившийся кожный жир и матировать кожу, в то же время не пересушивая ее. После использования пенка ваща кожа отлично очищена и увлажнена, не блестит и готова к нанесения макияжа.Способ применения: нанесите небольшое количество пенки на увлажненную водой кожу лица. Хорошо вспеньте и смойте теплой водой. В случае попадания в глаза - тщательно промойте теплой водой до прекращения неприятных ощущений.', '', '', '2014-12-08 12:55:33', '2014-12-08 12:58:58', 16),
(37, 'Глубоко очищающая пенка Pond''s', 'gluboko-ochishayushaya-penka-ponds', 0, 0, 1, 0, 'Эта мягкая пенка для лица содержит вулканическую глину. Добытая из недр земли, она обладает высокими абсорбирующими свойствами и глубоко проникает в поры. Впитывая в себя все накопившиеся в коже за день частички пыли и грязи, она бережно предотвращает появление черных точек. Глубоко очищающая пенка для умывания против черных точек POND''S быстро и эффективно удаляет кожный жир и отмерший слой клеток и делает кожу чистой и мягкой. Способ применения: нанесите небольшое количество пенки на увлажненную водой кожу лица. Хорошо вспеньте и смойте теплой водой. В случае попадания в глаза - тщательно промойте теплой водой до прекращения неприятных ощущений.', '', '', '2014-12-08 12:58:38', '2014-12-08 12:58:38', 16),
(38, 'Очищающая пенка против акне от Pond''s', 'ochishayushaya-penka-protiv-akne-ot-ponds', 0, 0, 1, 0, '<p>Очищающая пенка против акне от Pond&#39;s. Новика компании Pond&#39;s &mdash; средство по уходу за проблемной кожей. Пенка отлично пенится и глубоко очищает лицо, удаляет скопившиеся излишки кожного жира, пыль и грязь. При регулярном использовании пенка работает сразу в десяти направлениях, делая вашу кожу здоровой и красивой. Удаляет излишки кожного жира Предотвращает появление прыщей Делает пятна от уже прошедших прыщей менее яркими Уменьшает покраснения Снижает количество уже появившихся прыщиков Останавливает воспалительные процессы внутри прыщей Сглаживает шероховатости кожи Успокаивает кожу Сужает поры Придает коже внутреннее сияние.</p>\r\n', '', '', '2014-12-08 13:00:57', '2014-12-26 08:13:50', 16),
(39, 'Антивозрастнпя очищающая пенка Pond''s', 'antivozrastnpya-ochishayushaya-penka-ponds', 0, 0, 1, 0, 'Age Miracle Daily Regenerating Facial Foam – антивозрастная пенка для восстановления кожи и очищения от всемирно известной компании Pond''s. Пенка - микропилинг активно борется с признаками старения. Антивозрастная пенка для восстановления и омоложения кожи от Pond''s прекрасно очищает кожу, не оставляя ощущения сухости или стянутости. Антивозрастная пенка с омолаживающим эффектом содержит микрошарики, которые мягко очищают кожу и отшелушивают омертвевшие клетки кожного покрова. Пенка для умывания повышает влагоудерживающую способность кожи и поддерживает водный баланс кожного покрова на протяжении целого дня. Антивозрастная восстанавливающая пенка - микропилинг оставляет ощущение свежести и мягкости после своего использования. Антивозрастная пенка для очищения способствует восстановлению и сохранению молодости кожного покрова. Инновационная формула косметического средства не пересушивает кожу и при соприкосновении с водой образует нежную кремообразную пенку. Пенка – микропилинг с восстанавливающим эффектом замечательно увлажняет и разглаживает морщины. Антивозрастная пенку для омоложения и восстановления кожи рекомендуется применять ежедневно утром и вечером, в качестве первого шага ежедневного ухода за кожей лица. Кроме того, антивозрастную пенку можно использовать для обновления и придания свежести при необходимости.', '', '', '2014-12-08 13:03:09', '2014-12-08 13:03:09', 16),
(40, 'Омолаживающая пенка для умывания Pond''s', 'omolazhtvayushaya-penka-dlya-umyvaniya-ponds', 0, 0, 1, 0, 'Анти возрастная пенка для умывания - Золотое Сияние. Пенка обладает великолепной сливочной формулой с микрочастицами золота,разработанна специально для борьбы со старением кожи. Состав: золото и минералы которые помогают восстановить клетки кожи и сделать вашу кожу более сияющей мгновенно.', '', '', '2014-12-08 13:05:49', '2014-12-10 07:46:54', 16),
(42, 'Ароматический шампунь – формула Экстра Блеск', 'aromaticheskij-shampun-formula-ekstra-blesk', 0, 0, 1, 0, 'Ароматический шампунь – формула Экстра Блеск\r\nС органическим маслом оливы и органическим кунжутным маслом.\r\nОрганическое оливковое масло и масло рисовых отрубей, богатые витамином Е, увлажняют и смягчают волосы, органическое кунжутное масло действует как антиоксидант. Масла апельсина и танжерина, содержащие натуральные фруктовые кислоты (АНА) придают волосам сияние и дарят вдохновляющий аромат.', '', '', '2014-12-10 07:55:27', '2014-12-10 07:55:27', 5),
(43, 'Натуральный кондиционер для волос Thann Aromatic Wood', 'naturalnyj-kondicioner-dlya-volos-thann-aromatic-wood', 0, 0, 1, 0, 'Натуральный кондиционер для волос\r\nс белком пшеницы, маслом жожоба, эфирными маслами апельсина и мускатного ореха.\r\nМасло жожоба, белок пшеницы и эфирные масла обеспечивают волосам питание, дарят мягкость, действуют как антистатик. Масло апельсина увлажняет волосы. Масло мускатного ореха обладает антисептическими свойствами.', '', '', '2014-12-10 08:05:32', '2014-12-10 08:11:39', 5),
(44, 'Ароматический соляной скраб Aromatic Wood', 'aromaticheskij-solyanoj-skrab-aromatic-wood', 0, 0, 0, 0, '<p>Ароматический соляной скраб с маслом рисовых отрубей и эфирными маслами апельсина и мускатного ореха. Объединяет в себе полезные свойства масла рисовых отрубей, отшелушивающей морской соли, натуральных эфирных масел апельсина и мускатного ореха. Хорошо убирает омертвевшие клетки,увлажняет и питает. Натуральный продукт</p>\r\n', '', '', '2014-12-10 08:09:07', '2014-12-29 15:08:07', 5),
(45, 'Ароматический гель для душа Aromatic Wood', 'aromaticheskij-gel-dlya-dusha-aromatic-wood', 0, 0, 1, 0, 'Ароматический гель для душа\r\nс маслами апельсина и мускатного ореха.\r\nОптимизирует естественный баланс увлажненности кожи благодаря содержанию белка пшеницы и натуральных эфирных масел. Масло апельсина очищает и увлажняет кожу. Масло мускатного ореха обладает антисептическими свойствами.', '', '', '2014-12-10 08:11:12', '2014-12-10 08:11:12', 5),
(46, 'Крем для душа Aromatic Wood', 'krem-dlya-dusha-aromatic-wood', 0, 0, 1, 0, 'Крем для душа\r\nс органическим кокосовым маслом и маслом рисовых отрубей, 300 мл\r\nНасыщенная формула крема душа содержит 8,5% Органического кокосового масла, масла рисовых отрубей и масла жожоба. Интенсивно очищает и увлажняет кожу. Органическое кокосовое масло прекрасно смягчает, питает, увлажняет и успокаивает кожу. Масло рисовых отрубей, богатое витамином Е и жирными кислотами, которые действуют как мощные смягчающие компоненты, защищает кожу от потери влаги и действия свободных радикалов, замедляет процесс старения.', '', '', '2014-12-10 08:13:16', '2014-12-10 08:13:16', 5),
(47, 'Молочко для тела с рисовым экстрактом Aromatic Wood', 'molochko-dlya-tela-s-risovym-ekstraktom-aromatic-wood', 0, 0, 1, 0, 'Молочко для тела с рисовым экстрактом\r\nс маслами апельсина, танжерина и мускатного ореха, 320 мл\r\nСодержит масло рисовых отрубей, богатое витамином Е и насыщенными жирными кислотами. Дарит теплый и балансирующий аромат масел мускатного ореха, апельсина и танжерина.', '', '', '2014-12-10 08:16:38', '2014-12-10 08:16:38', 5),
(48, 'Масло для ванной и массажа Aromatic Wood', 'maslo-dlya-vannoj-i-massazha-aromatic-wood', 0, 0, 1, 0, 'Масло для ванной и массажа\r\nС маслом рисовых отрубей и эфирными маслами апельсина и мускатного ореха, 295 мл\r\nОбъединяет в себе полезные свойства масла рисовых отрубей и натуральных эфирных масел. Аромат апельсина и мускатного ореха дарит радость и эмоциональный подъем.', '', '', '2014-12-10 08:19:42', '2014-12-10 08:19:42', 5),
(49, 'Ароматическая свеча Aromatic Wood', 'aromaticheskaya-svecha-aromatic-wood', 0, 0, 0, 0, 'Ароматическая свеча\r\nСвеча без копоти и дыма на основе 100% пальмового масла без парафина и искусственных ароматизаторов. Масла апельсина и мускатного ореха придают радость и чувство одухотворенности.', '', '', '2014-12-10 08:23:55', '2014-12-14 07:09:56', 5),
(50, 'Эфирное масло апельсина и мускатного ореха', 'efirnoe-maslo-apelsina-i-muskatnogo-oreha', 0, 0, 1, 0, 'Эфирное масло\r\n100% натуральное эфирное масло без искусственных красителей и ароматизаторов. Масла апельсина и мускатного ореха придают радость и чувство одухотворенности.', '', '', '2014-12-10 08:26:04', '2014-12-10 09:01:18', 5),
(51, 'Лосьон для рук с органическими маслами ши и жожоба', 'loson-dlya-ruk-s-organicheskimi-maslami-shi-i-zhozhoba', 0, 0, 1, 0, 'Лосьон для рук\r\nс органическими маслами ши и жожоба.\r\nАроматический лосьон для рук с органическими маслами ши и жожоба и экстрактом водорослей питает кожу рук, делая ее более мягкой и здоровой. Гамма-Оризанол масла рисовых отрубей увлажняет кожу и защищает ее от ультрафиолетовых лучей. Нежирная формула лосьона обеспечивает хорошее впитывание. Экстракт водорослей и натуральные масла делают кожу эластичной.', '', '', '2014-12-10 08:28:36', '2014-12-10 08:28:36', 5),
(52, 'Универсальный бальзам Oriental Essence', 'universalnyj-balzam-oriental-essence', 0, 0, 0, 0, 'Универсальный бальзам\r\nс органическим маслом жожоба, органическим пчелиным воском и экстрактом граната.\r\nМягкая текстура бальзама увлажняет и питает кожу, защищает от обезвоживания. Содержащиеся в нем органические масло жожоба и пчелиный воск сертифицированы Министерством сельского хозяйства США. Гранат обеспечивает длительное увлажнение. Воск подсолнечника богат витамином Е, обладает противовоспалительным свойством, предотвращает сухость и действует как антиоксидант. Заживляет ранки и трещины на коже. Масла каффир лайма и лемонграсса дарят тонизирующий аромат.\r\nБальзам упакован в стильный чехол.', '', '', '2014-12-10 08:32:22', '2014-12-10 08:32:22', 5),
(53, 'Ароматический шампунь – формула Экстра Питание', 'aromaticheskij-shampun-formula-ekstra-pitanie', 0, 0, 1, 0, 'Ароматический шампунь – формула Экстра Питание\r\nС органическим маслом камелии и органическим аргановым маслом.\r\nОрганическое масло камелии и масло рисовых отрубей, богатые витамином Е, увлажняют и смягчают волосы, оздоравливают кожу головы. Органическое аргановое масло и про-витамин В5 укрепляют и разглаживают волосы. Масла лемонграсса и каффир лайма, содержащие натуральные фруктовые кислоты (АНА) очищают волосы и дарят тонизирующий аромат.', '', '', '2014-12-10 08:36:19', '2014-12-10 08:36:19', 5),
(54, 'Натуральный кондиционер для волос с белком пшеницы и маслом жожоба', 'naturalnyj-kondicioner-dlya-volos-s-belkom-pshenicy-maslom-zhozhoba-efirnymi-maslami-lemongrassa-i-kaffir-lajma', 0, 0, 1, 0, 'Натуральный кондиционер для волос\r\nс белком пшеницы, маслом жожоба, эфирными маслами лемонграсса и каффир лайма.\r\nМасло жожоба, белок пшеницы и эфирные масла обеспечивают волосам питание, дарят мягкость, действуют как антистатик. Масло каффир лайма очищает и увлажняет волосы. Масло лемонграсса обладает антисептическими свойствами.', '', '', '2014-12-10 08:39:05', '2014-12-17 08:35:46', 5),
(55, 'Ароматический соляной скраб Oriental Essence', 'aromaticheskij-solyanoj-skrab-oriental-essence', 0, 0, 0, 0, '<p>Ароматический соляной скраб с маслом рисовых отрубей и эфирными маслами лемонграсса и каффир лайма. Объединяет в себе полезные свойства масла рисовых отрубей, отшелушивающей морской соли, натуральных эфирных масел лемонграсса и каффир лайма.</p>\r\n', '', '', '2014-12-10 08:42:25', '2014-12-29 15:07:45', 5),
(56, 'Ароматический гель для душа Oriental Essence', 'aromaticheskij-gel-dlya-dusha-oriental-essence', 0, 0, 1, 0, 'Ароматический гель для душа\r\nс маслом рисовых отрубей, маслами каффир лайма и лемонграсса , 320 мл\r\nОптимизирует естественный баланс увлажненности кожи благодаря содержанию белка пшеницы и натуральных эфирных масел. Масло каффир лайма очищает и увлажняет кожу. Масло лемонграсса обладает антисептическими свойствами.', '', '', '2014-12-10 08:44:00', '2014-12-10 08:44:00', 5),
(57, 'Рисовое мыло с рисовым скрабом и эфирными маслами апельсина и мускатного ореха.', 'risovoe-mylo-s-risovym-skrabom-i-efirnymi-maslami-apelsina-i-muskatnogo-oreha', 0, 0, 0, 0, '<p>Рисовое мыло с рисовым скрабом и эфирными маслами апельсина и мускатного ореха. Объединяет в себе питательные свойства масла рисовых отрубей и растительных эфирных масел. Масло апельсина очищает и увлажняет кожу. Масло мускатного ореха обладает антисептическими свойствами.</p>\r\n', '', '', '2014-12-10 08:46:17', '2014-12-29 14:50:56', 5),
(58, 'Крем для душа Oriental Essence', 'krem-dlya-dusha-oriental-essence', 0, 0, 1, 0, 'Крем для душа\r\nБогатая формула крема для душа содержит 8,5% органического кокосового масла, масло рисовых отрубей и масло жожоба. Интенсивно очищает, обеспечивает питание, увлажнение и успокаивает кожу. Масло рисовых отрубей, богатое витамином Е и натуральными жирными кислотами, смягчает кожу, защищает её от потери влаги и действия свободных радикалов, замедляет процесс старения. Дарит экзотический и тонизирующий аромат масел каффирского лайма и лемонграсса. Масло лемонграсса стимулирует кровообращение и оказывает антисептическое действие.', '', '', '2014-12-10 08:48:05', '2014-12-10 08:48:05', 5),
(59, 'Молочко для тела с рисовым экстрактом, маслами каффир лайма и лемонграсса', 'molochko-dlya-tela-s-risovym-ekstraktom-maslami-kaffir-lajma-i-lemongrassa', 0, 0, 1, 0, 'Молочко для тела с рисовым экстрактом\r\nс маслом рисовых отрубей, маслами каффир лайма и лемонграсса.\r\nСодержит масло рисовых отрубей, богатое витамином Е и насыщенными жирными кислотами. Дарит экзотический и тонизирующий аромат масел лемонграсса и каффир лайма.', '', '', '2014-12-10 08:53:29', '2014-12-10 08:53:29', 5),
(60, 'Масло для ванной и массажа Oriental Essence', 'maslo-dlya-vannoj-i-massazha-oriental-essence', 0, 0, 1, 0, 'Масло для ванной и массажа\r\nС маслом рисовых отрубей и эфирными маслами лемонграсса и каффир лайма.\r\nОбъединяет в себе полезные свойства масла рисовых отрубей и натуральных эфирных масел. Масло лемонграсса очищает кожу. Масло каффир лайма освежает и способствует хорошему сну.', '', '', '2014-12-10 08:59:50', '2014-12-10 08:59:50', 5),
(61, 'Эфирное масло каффир лайма и лемонграсса', 'efirnoe-maslo-kaffir-lajma-i-lemongrassa', 0, 0, 1, 0, 'Эфирное масло\r\n100% натуральное эфирное масло без искусственных красителей и ароматизаторов. Масла каффир лайма и лемонграсса который освежают сознание и окунают в тайну Востока.', '', '', '2014-12-10 09:03:41', '2014-12-10 09:03:41', 5),
(62, 'Ароматическая свеча Oriental Essense', 'aromaticheskaya-svecha-oriental-essense', 0, 0, 0, 0, 'Ароматическая свеча\r\nСвеча без копоти и дыма на основе 100% пальмового масла без парафина и искусственных ароматизаторов. Масла каффир лайма и лемонграсса который освежают сознание и окунают в тайну Востока.', '', '', '2014-12-10 09:05:51', '2014-12-14 07:08:35', 5),
(63, 'Ароматический шампунь – формула против перхоти', 'aromaticheskij-shampun-formula-protiv-perhoti', 0, 0, 1, 0, 'Ароматический шампунь – формула против перхоти\r\nс протеином пшеницы, эфирными маслами эвкалипта, перечной мяты, тимьяна и розмарина.\r\nПротеин пшеницы увлажняет и смягчает волосы. Масло эвкалипта обладает антисептическими свойствами. Масло розмарина является мощным антиоксидантом. Масло перечной мяты освежает тело и дух. Масло тимьяна очищает и эффективно борется с перхотью.', '', '', '2014-12-10 09:07:53', '2014-12-10 09:07:53', 5),
(64, 'Ароматический гель для душа с протеином пшеницы, маслами тимьяна, розмарина', 'aromaticheskij-gel-dlya-dusha-s-proteinom-pshenicy-maslami-timyana-rozmarina-i-perechnoj-myaty', 0, 0, 1, 0, 'Ароматический гель для душа\r\nс протеином пшеницы, маслами тимьяна, розмарина и перечной мяты.\r\nОптимизирует естественный баланс увлажненности кожи благодаря содержанию белка пшеницы и натуральных эфирных масел. Масла тимьяна и розмарина защищают кожу от воздействия свободных. Масло перечной мяты освежает тело и дух.', '', '', '2014-12-10 09:09:15', '2014-12-17 08:37:31', 5),
(65, 'Молочко для тела с рисовым экстрактом Sea Foam', 'molochko-dlya-tela-s-risovym-ekstraktom-sea-foam', 0, 0, 1, 0, 'Молочко для тела с рисовым экстрактом\r\nс маслом рисовых отрубей, маслами эвкалипта, перечной мяты, тимьяна и розмарина, 320 мл\r\nСодержит масло рисовых отрубей, богатое витамином Е и насыщенными жирными кислотами. Дарит прохладный и освежающий аромат эвкалипта, перечной мяты, тимьяна и розмарина.', '', '', '2014-12-10 09:11:01', '2014-12-10 09:11:01', 5),
(66, 'Жидкость для мытья рук Sea Foam', 'zhidkost-dlya-mytya-ruk-sea-foam', 0, 0, 1, 0, 'Жидкость для мытья рук\r\nс экстрактом зеленого чая и маслом чайного дерева.\r\nТщательно очищает и бережно увлажняет кожу рук благодаря формуле с маслами рисовых отрубей и подсолнечника. Масло чайного дерева обладает антисептическими свойствами. Экстракт чайного дерева действует как мощный антиоксидант и защищает кожу от действия свободных радикалов. Экстракт алоэ вера освежает кожу и снимает раздражения.', '', '', '2014-12-10 09:12:39', '2014-12-10 09:12:39', 5),
(67, 'Лосьон для рук с органическими маслами ши и жожоба и экстрактом водорослей алгае', 'loson-dlya-ruk-s-organicheskimi-maslami-shi-i-zhozhoba-i-ekstraktom-vodoroslej-algae', 0, 0, 1, 0, 'Лосьон для рук\r\nс органическими маслами ши и жожоба и экстрактом водорослей алгае.\r\nПитает и увлажняет кожу рук. Y-оризанол из масла рисовых отрубей защищает кожу от вредного воздействия УФ-лучей и свободных радикалов. Нежирная формула моментально впитывается, не оставляя следов. Экстракт алгае повышает эластичность кожи.', '', '', '2014-12-10 09:14:18', '2014-12-10 09:14:18', 5),
(68, 'Рисовое мыло с рисовым скрабом и эфирными маслами эвкалипта, тимьяна и розмарина', 'risovoe-mylo-s-risovym-skrabom-i-efirnymi-maslami-evkalipta-timyana-i-rozmarina', 0, 0, 0, 0, '<p>Рисовое мыло с рисовым скрабом и эфирными маслами эвкалипта, тимьяна и розмарина, 100 гр Объединяет в себе питательные свойства масла рисовых отрубей и растительных эфирных масел. Масла тимьяна и розмарина защищают кожу от воздействия свободных. Масло эвкалипта обладает антисептическими свойствами.</p>\r\n', '', '', '2014-12-10 09:16:15', '2014-12-29 14:51:15', 5),
(69, 'Ароматическая свеча c маслом тимьяна и розмарина', 'aromaticheskaya-svecha-c-maslom-timyana-i-rozmarina', 0, 0, 0, 0, 'Ароматическая свеча\r\nСвеча без копоти и дыма на основе 100% пальмового масла без парафина и искусственных ароматизаторов. Масло перечной мяты концентрирует сознание. Эвкалипт полезен для здоровья верхних дыхательных путей. Масла тимьяна и розмарина оздоравливают тело и дух.', '', '', '2014-12-10 09:20:46', '2014-12-14 07:09:19', 5),
(70, 'Эфирное масло тимьяна и розмарина', 'efirnoe-maslo-timyana-i-rozmarina', 0, 0, 1, 0, 'Эфирное масло\r\n100% натуральное эфирное масло без искусственных красителей и ароматизаторов. Масло перечной мяты концентрирует сознание. Эвкалипт полезен для здоровья верхних дыхательных путей. Масла тимьяна и розмарина оздоравливают тело и дух.', '', '', '2014-12-10 09:23:38', '2014-12-10 09:23:38', 5),
(71, 'Бальзам для губ с рисовым экстрактом Thann', 'balzam-dlya-gub-s-risovym-ekstraktom-thann', 0, 0, 0, 0, 'Бальзам для губ с рисовым экстрактом\r\nс маслом рисовых отрубей, маслом ши и экстрактом алоэ вера.\r\nУникальный набор растительных экстрактов создает идеальный бальзам для губ. Масло рисовых отрубей обеспечивает борьбу со свободными радикалами, алоэ вера снимает раздражение, масло ши увлажняет и препятствует потере влаги. Бальзам смягчает, разглаживает и защищает нежную кожу губ.\r\nБальзам идет в стильном чехле', '', '', '2014-12-10 09:27:27', '2014-12-10 09:27:27', 5),
(72, 'Овсяный скраб для лица Thann', 'ovsyanyj-skrab-dlya-lica-thann', 0, 0, 0, 0, '<p>Овсяный скраб для лица с маслом рисовых отрубей и экстрактом алтея лекарственного. Овсяный скраб для лица нежно удаляет отмершие клетки кожи, масло рисовых отрубей питает кожу. Экстракт алтея лекарственного, содержащий витамины А, Е и С увлажняет и укрепляет кожу.</p>\r\n', '', '', '2014-12-10 09:29:13', '2014-12-29 14:49:18', 5);
